mapper.select:
SELECT * FROM students


        AND student_no LIKE CONCAT('%', #{studentNo}, '%')


        AND name LIKE CONCAT('%', #{name}, '%')


        AND major = #{major}


        AND class_name = #{className}


        AND status = #{status}


ORDER BY created_time DESC

SELECT COUNT(*) FROM students


        AND student_no LIKE CONCAT('%', #{studentNo}, '%')


        AND name LIKE CONCAT('%', #{name}, '%')


        AND major = #{major}


        AND class_name = #{className}


        AND status = #{status}
